using System;
using System.Collections.Generic;
using System.Text;
using System.IO;



namespace TC57CIM.IEC61970.Base.ICCPConfiguration {
	/// <summary>
	/// Specifies the expected security mechanism, per IEC 62351-4, to be utilized.
	/// </summary>
	public enum ApplicationSecurityKind : int {

		/// <summary>
		/// Indicates that an application security shall not be used in order to allow
		/// establishment of an MSS association.
		/// </summary>
		nonSecure,
		/// <summary>
		/// Indicates that the A-security-profile as specified in Clause 11 (or IEC TS
		/// 62351-4:2007) shall be used in order to allow establishment of an MMS
		/// association.
		/// </summary>
		secure,
		/// <summary>
		/// Indicates that E2E-security shall be used in order to allow establishment of an
		/// MMS association.
		/// </summary>
		endToEndSecure

	}//end ApplicationSecurityKind

}//end namespace ICCPConfiguration